// frontend/src/pages/AddAuditRecord.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../services/api";
import "../styles/addaudit.css";
import Layout from "../components/Layout";

function AddAuditRecord() {
  const navigate = useNavigate();
  const userId = localStorage.getItem("user_id");

  const [step, setStep] = useState(1);
  const [action, setAction] = useState("");          // "create" | "modify" | "delete"
  const [patientId, setPatientId] = useState("");

  const [formData, setFormData] = useState({
    patient_name: "",
    age: "",
    gender: "",
    diagnosis: "",
    medication: "",
    data: "",                                       // kept as "data" to match backend (mapped to notes)
    visit_date: new Date().toISOString().split("T")[0],
    vitals: "",
  });

  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [patientFound, setPatientFound] = useState(false); // just to show a small banner

  const handleChange = (e) => {
    setFormData((f) => ({ ...f, [e.target.name]: e.target.value }));
  };

  /**
   * STEP 1: Check patient and move forward.
   * NEW LOGIC:
   * - If action === "create": ALWAYS allow creating another log.
   *   If patient exists, prefill (name/age/gender) from latest log.
   * - If action === "modify" or "delete": require existing logs for that patient.
   */
  const handleNext = async () => {
    if (!action || !patientId) {
      setError("Please enter patient ID and select an action.");
      return;
    }

    try {
      setError("");
      // Pull ALL logs by this doctor (as before) – we’ll search for this patient locally
      const params = { user_id: userId, role: "doctor" };
      const resp = await axios.get("/api/audit/logs", { params });
      const allLogs = Array.isArray(resp?.data?.logs) ? resp.data.logs : [];

      // Find logs for this patient and sort by timestamp (latest first)
      const patientLogs = allLogs
        .filter((l) => String(l.patient_id) === String(patientId))
        .sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));

      const latest = patientLogs[0];

      if (action === "modify" || action === "delete") {
        if (!latest) {
          setError("Patient does not exist. You can only modify/delete an existing patient’s records.");
          return;
        }
        // Route to modify/delete flows as you already had
        if (action === "modify") {
          navigate("/modify", { state: { patientId } });
        } else {
          navigate("/delete", { state: { patientId } });
        }
        return;
      }

      // action === "create" → ALWAYS allow adding another visit/log
      if (latest) {
        // Prefill demographics from the latest record to save typing
        setPatientFound(true);
        setFormData((f) => ({
          ...f,
          patient_name: latest.patient_name || f.patient_name,
          age: latest.age || f.age,
          gender: latest.gender || f.gender, // will show blank if you don't store gender in logs
        }));
      } else {
        setPatientFound(false);
      }

      setStep(2);
    } catch (err) {
      console.error("Error checking patient:", err?.response?.data || err.message);
      setError("Error checking patient records.");
    }
  };

  /**
   * STEP 2: Submit a new log (another visit) for this patient.
   * We normalize action to uppercase before sending, but backend accepts anything.
   */
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    const payload = {
      user_id: userId,
      patient_id: patientId,
      action: (action || "create").toUpperCase(), // e.g., "CREATE"
      ...formData,                                 // includes {patient_name, age, gender, diagnosis, medication, data, visit_date, vitals}
    };

    try {
      await axios.post("/api/audit/add-log", payload, {
        headers: { "Content-Type": "application/json" },
      });
      setShowSuccess(true);
      setTimeout(() => navigate("/dashboard"), 1500);
    } catch (err) {
      console.error("API error:", err?.response?.data || err.message);
      setError("❌ Failed to submit record.");
    } finally {
      setLoading(false);
    }
  };

  // --- UI ---
  return (
    <Layout>
      <div className="addaudit-wrapper">
        <div className="addaudit-card">
          <div className="addaudit-header">
            <button className="back-btn" onClick={() => navigate("/dashboard")}>
              <i className="bi bi-arrow-left" /> Back
            </button>
            <h2><i className="bi bi-plus-circle" /> Add Patient Record</h2>
          </div>

          {step === 1 && (
            <>
              <div className="form-group">
                <label>Select Action</label>
                <select
                  className="form-control"
                  value={action}
                  onChange={(e) => setAction(e.target.value)}
                >
                  <option value="">-- Select Action --</option>
                  <option value="create">Create (add a new visit/log)</option>
                  <option value="modify">Modify</option>
                  <option value="delete">Delete</option>
                </select>
              </div>

              <div className="form-group">
                <label>Enter Patient ID</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Patient ID"
                  value={patientId}
                  onChange={(e) => setPatientId(e.target.value)}
                />
              </div>

              {error && <div className="text-danger mb-2">{error}</div>}

              <button className="btn btn-primary w-100" onClick={handleNext}>
                Next
              </button>
            </>
          )}

          {step === 2 && (
            <form onSubmit={handleSubmit} className="mt-2">
              {patientFound && (
                <div className="alert alert-success mb-3">
                  Existing patient found — prefilling details. You can still change them and add a **new** log.
                </div>
              )}

              <div className="two-col">
                <div className="form-group">
                  <label>Patient Name</label>
                  <input
                    type="text"
                    name="patient_name"
                    className="form-control"
                    value={formData.patient_name}
                    onChange={handleChange}
                    placeholder="Full name"
                  />
                </div>

                <div className="form-group">
                  <label>Age</label>
                  <input
                    type="number"
                    name="age"
                    className="form-control"
                    value={formData.age}
                    onChange={handleChange}
                    placeholder="Age"
                  />
                </div>

                <div className="form-group">
                  <label>Gender</label>
                  <input
                    type="text"
                    name="gender"
                    className="form-control"
                    value={formData.gender}
                    onChange={handleChange}
                    placeholder="Gender"
                  />
                </div>

                <div className="form-group">
                  <label>Diagnosis</label>
                  <input
                    type="text"
                    name="diagnosis"
                    className="form-control"
                    value={formData.diagnosis}
                    onChange={handleChange}
                    placeholder="Diagnosis"
                  />
                </div>

                <div className="form-group">
                  <label>Medication</label>
                  <input
                    type="text"
                    name="medication"
                    className="form-control"
                    value={formData.medication}
                    onChange={handleChange}
                    placeholder="Medication"
                  />
                </div>

                <div className="form-group">
                  <label>Notes</label>
                  <textarea
                    name="data" // kept as "data" so backend maps to notes
                    className="form-control"
                    rows={3}
                    value={formData.data}
                    onChange={handleChange}
                    placeholder="Visit notes (symptoms, observations, etc.)"
                  />
                </div>

                <div className="form-group">
                  <label>Visit Date</label>
                  <input
                    type="date"
                    name="visit_date"
                    className="form-control"
                    value={formData.visit_date}
                    onChange={handleChange}
                  />
                </div>

                <div className="form-group">
                  <label>Vitals</label>
                  <input
                    type="text"
                    name="vitals"
                    className="form-control"
                    value={formData.vitals}
                    onChange={handleChange}
                    placeholder='e.g. {"bp":"120/80","hr":72}'
                  />
                </div>
              </div>

              {error && <div className="text-danger mb-2">{error}</div>}
              {showSuccess && (
                <div className="alert alert-success mb-2">
                  ✅ Record added successfully!
                </div>
              )}

              <div className="d-flex gap-2">
                <button type="submit" className="btn btn-primary" disabled={loading}>
                  {loading ? "Saving..." : "Submit Record"}
                </button>
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => setStep(1)}
                >
                  Back
                </button>
              </div>
            </form>
          )}
        </div>
      </div>
    </Layout>
  );
}

export default AddAuditRecord;
