import React, { useState } from "react";
import Layout from "../components/Layout";
import "../styles/loginregister.css";
import axios from "../services/api";

export default function LoginRegisterPage() {
  const [tab, setTab] = useState("login");
  const [login, setLogin] = useState({ user_id: "", password: "", role: "" });
  const [reg, setReg] = useState({ user_id: "", password: "", role: "" });
  const [msg, setMsg] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    setMsg("");
    try {
      const { data } = await axios.post("/api/auth/login", login);
      // expect: {message, role, user_id, patient_id?}
      localStorage.setItem("user_id", data.user_id);
      localStorage.setItem("role", data.role);
      if (data.patient_id) localStorage.setItem("patient_id", data.patient_id);
      window.location.href = "/dashboard";
    } catch {
      setMsg("Invalid credentials.");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setMsg("");
    try {
      const { data } = await axios.post("/api/auth/register", reg);
      setMsg("✅ Registration successful. You can log in now.");
      setTab("login");
    } catch {
      setMsg("Registration failed.");
    }
  };

  return (
    <Layout>
      <div className="auth-shell">
        <div className="card auth-card">
          {/* Left: hero copy */}
          <div className="auth-left">
            <div className="h1">Welcome!</div>
            <p className="sub">
              Secure, tamper-evident audit logging for EHR. Role-based access,
              encryption, and blockchain-style hash chaining.
            </p>
            <div className="tech-row">
              <span className="pill">FastAPI</span>
              <span className="pill">PostgreSQL</span>
              <span className="pill">AES/RSA</span>
            </div>
          </div>

          {/* Right: forms */}
          <div className="auth-right">
            <div className="tabs">
              <button
                className={`tab ${tab === "login" ? "active" : ""}`}
                onClick={() => setTab("login")}
              >
                Login
              </button>
              <button
                className={`tab ${tab === "register" ? "active" : ""}`}
                onClick={() => setTab("register")}
              >
                Register
              </button>
            </div>

            {tab === "login" ? (
              <form className="stack" onSubmit={handleLogin}>
                <input
                  className="input"
                  placeholder="User ID"
                  value={login.user_id}
                  onChange={e => setLogin({ ...login, user_id: e.target.value })}
                  required
                />
                <input
                  className="input"
                  type="password"
                  placeholder="Password"
                  value={login.password}
                  onChange={e => setLogin({ ...login, password: e.target.value })}
                  required
                />
                <select
                  className="select"
                  value={login.role}
                  onChange={e => setLogin({ ...login, role: e.target.value })}
                  required
                >
                  <option value="">Select Role</option>
                  <option value="doctor">Doctor</option>
                  <option value="patient">Patient</option>
                  <option value="auditor">Auditor</option>
                </select>
                <button className="button primary" type="submit">Login</button>
              </form>
            ) : (
              <form className="stack" onSubmit={handleRegister}>
                <input
                  className="input"
                  placeholder="User ID"
                  value={reg.user_id}
                  onChange={e => setReg({ ...reg, user_id: e.target.value })}
                  required
                />
                <input
                  className="input"
                  type="password"
                  placeholder="Password"
                  value={reg.password}
                  onChange={e => setReg({ ...reg, password: e.target.value })}
                  required
                />
                <select
                  className="select"
                  value={reg.role}
                  onChange={e => setReg({ ...reg, role: e.target.value })}
                  required
                >
                  <option value="">Select Role</option>
                  <option value="doctor">Doctor</option>
                  <option value="patient">Patient</option>
                  <option value="auditor">Auditor</option>
                </select>
                <button className="button primary" type="submit">Create Account</button>
              </form>
            )}

            {msg && <div className="auth-msg">{msg}</div>}
          </div>
        </div>
      </div>
    </Layout>
  );
}
